    private void calc (){
        double num1 = Double.parseDouble(txtNum1.getText());
        double num2 = Double.parseDouble(txtNum2.getText());
       
        double res=0;
        
        if (txtOpe.getText().equals("+")){
            res =num1+num2;
        }
        if (txtOpe.getText().equals("-")){
            res =num1-num2;
        }
        if (txtOpe.getText().equals("*")){
            res =num1*num2;
        }
        if (txtOpe.getText().equals("/")){
            res =num1/num2;
        }
        if (txtOpe.getText().equals("%")){
            res =num1%num2;
        }
        
        txtRes.setText(String.valueOf(res));
    }
